import _objectSpread from "@babel/runtime-corejs2/helpers/esm/objectSpread";
import { SET_ENG, SET_PL } from "../actions/switchActions";
import TextService from "../../config/text.service";
const initialState = {
  isPl: true,
  language: TextService.textPl
};
export default ((state = initialState, action) => {
  switch (action.type) {
    case SET_ENG:
      return _objectSpread({}, state, {
        isPl: false,
        language: TextService.textEng
      });

    case SET_PL:
      return _objectSpread({}, state, {
        isPl: true,
        language: TextService.textPl
      });

    default:
      return state;
  }
});
